insert into activity(code, isactive, name, namealt) values('1', true, 'т.1 Експлоатация на инсталации и съоръжения, за които се изисква издаване на комплексно разрешително по чл. 117 от Закона за опазване на околната среда, с изключение на инсталациите и съоръженията или частите от тях, използвани за изследователска работа, разработки и изпитания на нови продукти и процеси.', '1. Operation of any facilities and installations subject to a requirement for the issuance of an integrated permit under Article 117 of the Environmental Protection Act, with the exception of facilities and installations or parts of facilities and installations used for research, development and testing of new products and processes.');
insert into activity(code, isactive, name, namealt) values('2', true, 'т.2 За дейности по събиране, транспортиране, оползотворяване или обезвреждане на отпадъци, в т.ч. опасни отпадъци, за които се изисква разрешение или регистрационен документ съгласно Закона за управление на отпадъците.', '2. For any activities which comprehend collection, transport, recovery or disposal of waste, including hazardous waste, subject to permit or registration document according to the Waste Management Act.');
insert into activity(code, isactive, name, namealt) values('3', true, 'т.3 Извършване на дейности по използване на водите и водните обекти.', '3.  Carrying out any activities involving use of waters and water bodies, including: a) any discharges into surface water bodies of waste waters, whereby hazardous substances are being released, as indicated in the Ordinances under Article 135(1), items 9 and 17 of the Waters Act, for which prior regulation is required as part of the discharge permits issued in accordance with the Waters Act and in the integrated permits under the Environmental Protection Act; b) diversion of substances into ground waters, injection of pollutants into ground waters, water intake and impoundment of water bodies, for which issuance of authorisation is required within the meaning of the Waters Act.');
insert into activity(code, isactive, name, namealt) values('4', true, 'т.4 Извършване на дейности по производство, употреба, съхранение, обработка, пълнене и изпускане в околната среда на химични и смеси по смисъла на чл. 2 от Закона за защита от вредното въздействие на химичните вещества и смеси.', '4. Carrying out any activities on the production, use, storage, processing, filling and release into the environment of chemical substances and mixtures classified under Regulation (EC) No. 1272/2008 of the European Parliament and of the Council of 16 December 2008 on classification, labelling and packaging of substances and mixtures, amending and repealing Directives 67/548/EEC and 1999/45/EC, and amending Regulation (EC) No. 1907/2006 (OJ, L 353/1 of 31 December 2008).');
insert into activity(code, isactive, name, namealt) values('5', true, 'т.5 Извършване на дейности по производство, употреба, съхранение, обработка, пълнене и изпускане в околната среда на продукти за растителна защита по смисъла на Закона за защита на растенията.', '5. Carrying out any activities which comprehend manufacture, use, storage, processing, filling and release into the environment of plant protection products within the meaning given by the Plant Protection Act.');
insert into activity(code, isactive, name, namealt) values('6', true, 'т.6 Извършване на дейности по производство, употреба, съхранение, обработка, пълнене и изпускане в околната среда на биоциди по смисъла на Закона за защита от вредното въздействие на химичните вещества и смеси.', '6. Carrying out any activities which comprehend manufacture, use, storage, processing, filling and release into the environment of biocides within the meaning given by the Protection Against the Harmful Impact of Chemical Substances and Compounds Act.');
insert into activity(code, isactive, name, namealt) values('7', true, 'т.7 Извършване на дейности по превоз на опасни товари по смисъла на Закона за автомобилните превози, Закона за железопътния транспорт, Закона за морските пространства, вътрешните водни пътища и пристанищата на Република България и Закона за гражданското въздухоплаване.', '7. Carrying out any activities which comprehend transport of dangerous goods within the meaning given by the Carriage by Road Act, the Rail Transport Act, the Maritime Space, Inland Waterways and Ports of the Republic of Bulgaria Act, and the Civil Aviation Act.');
insert into activity(code, isactive, name, namealt) values('8', true, 'т.8 Извършване на дейности по работа с генетично модифицирани организми (ГМО) в контролирани условия, освобождаване на ГМО в околната среда, пускане на пазара на ГМО или комбинация от тях като продукти или съставка на продукти, пренасяне на ГМО, внос, износ и транзит на ГМО по смисъла на Закона за генетично модифицирани организми, за които дейности се изисква разрешение в съответствие със Закона за генетично модифицирани организми.', '8. Carrying out any activities which comprehend contained use of genetically modified organisms (GMOs), release of GMOs into the environment, placing on the market of a GMO or of a combination of GMOs as or in products, transfer of GMOs, import, export and transit of GMOs within the meaning given by the Genetically Modified Organisms Act, which activities are subject to authorization in accordance with the Genetically Modified Organisms Act.');
insert into activity(code, isactive, name, namealt) values('9', true, 'т.9 Извършване на дейности по превоз на отпадъци, в т.ч. внос, износ и транзит на отпадъци по смисъла на глава пета, раздел ІV от Закона за управление на отпадъците.', '9. Carrying out any activities which include transportation of waste, including export, import and transit of waste within the meaning of Section IV of Chapter Five of the Waste Management Act.');
insert into activity(code, isactive, name, namealt) values('10', true, 'т.10 Извършване на дейности по управление на минните отпадъци съгласно Закона за подземните богатства.', '10.  Carrying out any activities which comprehend management of extractive waste in accordance with the Subsurface Resources Act.');
insert into activity(code, isactive, name, namealt) values('11', true, 'т.11 Експлоатация на места за съхранение в съответствие със Закона за съхранение на въглероден диоксид в земните недра.', '11. Operation of storage sites in accordance with the Geological Storage of Carbon Dioxide Act.');